import R from 'ramda';

export const getPhoneById = (state, id) => {
return R.prop(id, state.phones)
};

export const getPhones = state => {
console.log(state.phonesPage.ids)

  const phones = R.map(id => getPhoneById(state, id), state.phonesPage.ids)
  console.log(phones)

  return phones
}

export const getRenderedPhoneLenght = state => R.length(state.phonesPage.ids)
